import { QuizContext } from './Helpers/Contexts';
import React, {useContext} from 'react';
import "./App.css";


function MainMenu() {
    const {gameState, setGameState} = useContext(QuizContext);
    return (
        <div className="Menu">
            <button 
            onClick={() => {
                setGameState("quiz");
            }}>Start Quiz</button>
        </div>
    );
}

export default MainMenu;
